import React, { useRef } from "react";
import { Button, Input, InputLabel, Grid } from "@material-ui/core";

const Upload = ({ file, formProps }) => {
  return (
    <Grid>
      <Input
        accept='application/x-gzip'
        type='file'
        name='file'
        onChange={(e) => formProps.setFieldValue(file, e.target.files[0])}
      />
      {formProps.errors.file && formProps.touched.name ? (
        <div>{formProps.errors.name}</div>
      ) : null}
    </Grid>
  );
};
export default Upload;
