import React from "react";
import {
  Button,
  Input,
  InputLabel,
  Grid,
  FilledInput,
} from "@material-ui/core";

const Upload = ({ file, formProps }) => {
  return (
    <Grid>
      <Input
        accept='application/gzip, .tar, .tar.gz, .gz'
        type='file'
        name='file'
        ref={fileInput}
        style={{ display: "none" }}
        onChange={(event) => {
          formProps.setFieldValue(file, event.target.files[0]);
        }}
      />
      <InputLabel htmlFor='contained-button-file'>
        <Button
          variant='outlined'
          component='span'
          onClick={() => FilledInput.current.click()}
        >
          Choose File
        </Button>
      </InputLabel>
    </Grid>
  );
};
export default Upload;
